--symbols in doctor_name field 
SELECT (select ABBREVIATION from SCHOOLS where students.SCHOOLID=SCHOOL_NUMBER) as School, 'Doctor Name' as "Error Location", 'Symbols in doctor name' as Error, students.LASTFIRST, to_char(students.STUDENT_NUMBER) as Student_Number, students.DOCTOR_NAME FROM students Where Enroll_Status in (0,-1) and students.DOCTOR_NAME is not null and students.DOCTOR_NAME like '%"%' and schoolid not in (999999, 43) and grade_level <> 13 and Enroll_Status = 0;

--doctor phn
