--medicaid number contains letters
SELECT (select ABBREVIATION from SCHOOLS where students.SCHOOLID=SCHOOL_NUMBER) as School, 'Medicaid Number' as "Error Location", 'Letters in Medicaid number' as Error, students.LASTFIRST, to_char(students.STUDENT_NUMBER) as Student_Number, /*translate numbers to blanks then trim the blanks*/ TRIM(TRANSLATE(s_sc_stu_x.medicaidid, '0123456789','          ')) FROM s_sc_stu_x, students where s_sc_stu_x.studentsdcid = students.dcid AND TRIM(TRANSLATE(s_sc_stu_x.medicaidid, '0123456789','          ')) is not null and schoolid <> '999999' and grade_level <> 13 and Enroll_Status in (0,-1);

