SELECT
TO_CHAR (S.STUDENT_NUMBER),
S.LASTFIRST,
S.GRADE_LEVEL,

CC.COURSE_NUMBER,
C.COURSE_NAME

FROM
STUDENTS S,
CC,
COURSES C,
TEACHERS T,
SECTIONS SE

WHERE
S.ID=CC.STUDENTID
AND SE.ID=CC.SECTIONID
AND T.ID=SE.TEACHER
AND C.COURSE_NUMBER=SE.COURSE_NUMBER
AND C.COURSE_NUMBER in ('37990000','37991000')
/*AND (C.COURSE_NUMBER LIKE '%AW' OR C.COURSE_NUMBER LIKE '%AH')*/
AND S.GRADE_LEVEL = 99
AND CC.SCHOOLID=84
